if(typeof require !== 'undefined') XLSX = require('xlsx.js');
var workbook = XLSX.readFile('../static/datafile/xmjd.xlsx');
var fs = require('fs');
console.log(workbook.SheetNames.length);
for(let n=0;n<workbook.SheetNames.length;n++){
    /* DO SOMETHING WITH workbook HERE */
    var sheet_name = workbook.SheetNames[n];
    /* Get worksheet */
    var worksheet = workbook.Sheets[sheet_name];
    let xmjd =XLSX.utils.sheet_to_json(worksheet);
    console.log("output"+sheet_name)

    fs.writeFile("../static/datafile/"+sheet_name+".txt", JSON.stringify(xmjd), function(err) {
    if (err) {
        console.log(err);
    }
    });
}
